+  display: none;
+}
+.sphx-glr-thumbcontainer:hover {
+  border: 1px solid;
+  border-color: var(--sg-thumb-hover-border);
+  cursor: pointer;
+}
+.sphx-glr-thumbcontainer a.internal {
+  bottom: 0;
+  display: block;
+  left: 0;
+  box-sizing: border-box;
+  padding: 150px 10px 0;
+  position: absolute;
+  right: 0;
